package main

import "fmt"

func longestPalindrome(s string) int {
	letterMap := make(map[byte]int)
	for i := 0; i < len(s); i++ {
		letterMap[s[i]]++
	}

	ans := 0
	for _, num := range letterMap {
		ans += num / 2 * 2
		if ans%2 == 0 && num%2 == 1 {
			ans++
		}
	}

	return ans
}

func main() {
	fmt.Printf("%v\n", longestPalindrome(""))
}
